package vn.phh235.bookstorebackend.entity;import jakarta.persistence.*;import jdk.jfr.Enabled;import lombok.Data;import java.util.List;@Entity@Data@Table(name = "nguoi_dung")public class NguoiDung {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "ma_nguoi_dung")    private int maNguoiDung;    @Column(name = "ho_dem", columnDefinition = "nvarchar(255)")    private String hoDem;    @Column(name = "ten", columnDefinition = "nvarchar(255)")    private String ten;    @Column(name = "ten_dang_nhap", columnDefinition = "nvarchar(255)")    private String tenDangNhap;    @Column(name = "mat_khau", columnDefinition = "nvarchar(512)")    private String matKhau;    @Column(name = "gioi_tinh", columnDefinition = "nvarchar(255)")    private String gioiTinh;    @Column(name = "email", columnDefinition = "nvarchar(255)")    private String email;    @Column(name = "so_dien_thoai", columnDefinition = "nvarchar(255)")    private String soDienThoai;    @Column(name = "dia_chi_mua_hang", columnDefinition = "nvarchar(255)")    private String diaChiMuaHang;    @Column(name = "dia_chi_giao_hang", columnDefinition = "nvarchar(255)")    private String diaChiGiaoHang;    @OneToMany(mappedBy = "nguoiDung", fetch = FetchType.LAZY, cascade = {            CascadeType.PERSIST, CascadeType.MERGE,            CascadeType.DETACH, CascadeType.REFRESH    })    private List<SuDanhGia> danhSachSuDanhGia;    @OneToMany(mappedBy = "nguoiDung", fetch = FetchType.LAZY, cascade = {            CascadeType.PERSIST, CascadeType.MERGE,            CascadeType.DETACH, CascadeType.REFRESH    })    private List<SachYeuThich> danhSachYeuThich;    @ManyToMany(fetch = FetchType.LAZY, cascade = {            CascadeType.PERSIST, CascadeType.MERGE,            CascadeType.DETACH, CascadeType.REFRESH    })    @JoinTable(            name = "nguoidung_quyen",            joinColumns = @JoinColumn(name = "ma_nguoi_dung"),            inverseJoinColumns = @JoinColumn(name = "ma_quyen")    )    private List<Quyen> danhSachQuyen;    @OneToMany(mappedBy = "nguoiDung", fetch = FetchType.LAZY, cascade = {            CascadeType.PERSIST, CascadeType.MERGE,            CascadeType.DETACH, CascadeType.REFRESH    })    private List<DonHang> danhSachDonHang;}